shaders.vertex:
precision highp float;

attribute vec3 aPos;
attribute vec2 aUV;

// Varyings
varying vec2 uv0;
varying vec2 uvDistort;

// Uniforms
uniform mat4 effects_ObjectToWorld;
uniform mat4 effects_MatrixVP;
uniform vec4 _Time;

uniform float _DistortTexUSpeed;
uniform float _DistortTexVSpeed;

void main() {
    uv0 = aUV;
    uvDistort = aUV + fract(vec2(_DistortTexUSpeed, _DistortTexVSpeed) * _Time.y);
    gl_Position = effects_MatrixVP * effects_ObjectToWorld * vec4(aPos, 1.0);
}

shaders.fragment:
precision highp float;

#define _FDISTORTTEX_ON
#define _MAINTEXUVS_POLAR
#define _FMASKTEX_ON
#define _FDISSOLVETEX_ON
#define _FADDTEX_ON

// Varyings
varying vec2 uv0;
varying vec2 uvDistort;

// Uniforms
uniform vec4 _Time;

uniform sampler2D _MainTex;
uniform vec4 _TillingOffset;
uniform vec4 _MainColor;
uniform float _MainTexAR;
uniform float _MainTexUVPolar;
uniform float _MainTexUSpeed;
uniform float _MainTexVSpeed;

uniform float _UseAddTex;
uniform sampler2D _AddTex;
uniform vec4 _AddColor;
uniform float _AddTexUSpeed;
uniform float _AddTexVSpeed;
uniform float _AddTexPower;
uniform float _AddTexIntensity;

uniform sampler2D _DistortTex;
uniform float _DistortIntensity;

uniform sampler2D _MaskTex;
uniform float _MaskTexReverse;
uniform float _MaskTexAR;
uniform float _MaskTexUSpeed;
uniform float _MaskTexVSpeed;

uniform sampler2D _DissolveTex;
uniform vec4 _DissolveColor;
uniform float _DissolveTexUSpeed;
uniform float _DissolveTexVSpeed;
uniform float _DissolveTexPower;
uniform float _DissolveTexIntensity;
uniform float _DissolveIntensity;
uniform float _DissolveSoft;
uniform float _DissolveWide;

void main() {
    vec2 uvMain = uv0;

    // 扭曲贴图
    #ifdef _FDISTORTTEX_ON
    vec2 uvDistort = uvDistort + fract(vec2(_MainTexUSpeed, _MainTexVSpeed) * _Time.y);
    vec4 var_DistortTex = texture2D(_DistortTex, uvDistort);
    float var_DistortTexAlpha = var_DistortTex.r - 0.5;
    var_DistortTexAlpha *= _DistortIntensity;
    vec2 DistortUV = vec2(var_DistortTexAlpha, var_DistortTexAlpha);
    uvMain += DistortUV;  // 扭曲主贴图UV
    #endif

    // 采样主纹理
    // 极坐标转化
    #ifdef _MAINTEXUVS_POLAR
    if(_MainTexUVPolar > 0.0) {
        vec2 centerUV = uvMain - vec2(0.5, 0.5);
        vec2 polarUV = vec2(length(centerUV) * 2.0, atan(centerUV.y, centerUV.x) * (1.0 / 6.28318)); // 6.28318 is 2*pi
        uvMain = polarUV;
    }
    #endif
    uvMain = uvMain * _TillingOffset.xy + _TillingOffset.zw;
    uvMain += fract(vec2(_MainTexUSpeed, _MainTexVSpeed) * _Time.y);
    vec4 mainTex = texture2D(_MainTex, uvMain);
    mainTex.rgb = (_MainTexAR == 0.0 ? mainTex.rgb : mainTex.rrr);
    float mainTexAlpha = (_MainTexAR == 0.0 ? mainTex.a : mainTex.r);
    vec3 color = mainTex.rgb * _MainColor.rgb;
    float alpha = mainTexAlpha * _MainColor.a;

    // 额外贴图
    if(_UseAddTex > 0.0) {
        vec2 uvAdd = uv0;
        #ifdef _ADDTEXUVS_POLAR
        vec2 addCenterUV = uvAdd - vec2(0.5, 0.5);
        vec2 addPolarUV = vec2(length(addCenterUV) * 2.0, atan(addCenterUV.y, addCenterUV.x) * (1.0 / 6.28318)); // 6.28318 is 2*pi
        uvAdd = addPolarUV;
        #endif
        uvAdd.xy += fract(vec2(_AddTexUSpeed, _AddTexVSpeed) * _Time.y);
        vec4 var_AddTex = texture2D(_AddTex, uvAdd);
        var_AddTex = clamp(pow(abs(var_AddTex), vec4(_AddTexPower)) * _AddTexIntensity, 0.0, 1.0);
        vec3 addColor = var_AddTex.rgb * _AddColor.rgb;
        float addAlpha = var_AddTex.a * _AddColor.a;
        color = mix(color, color + addColor, addAlpha);
    }

    // 遮罩贴图
    #ifdef _FMASKTEX_ON
    vec2 uvMask = uv0;
        #ifdef _MASKTEXUVS_POLAR
    vec2 maskCenterUV = uvMask - vec2(0.5, 0.5);
    vec2 maskPolarUV = vec2(length(maskCenterUV) * 2.0, atan(maskCenterUV.y, maskCenterUV.x) / (2.0 * PI));
    uvMask = maskPolarUV;
        #endif
    uvMask += fract(vec2(_MaskTexUSpeed, _MaskTexVSpeed) * _Time.y);
    vec4 var_MaskTex = texture2D(_MaskTex, uvMask);
    float var_MaskTexAlpha = (_MaskTexAR == 0.0 ? var_MaskTex.a : var_MaskTex.r);
    var_MaskTexAlpha = (_MaskTexReverse == 0.0 ? var_MaskTexAlpha : 1.0 - var_MaskTexAlpha);
    alpha *= var_MaskTexAlpha;
    #endif

    // 溶解效果
    #ifdef _FDISSOLVETEX_ON
    vec2 uvDissolve = uv0;
        #ifdef _DISSOLVETEXUVS_POLAR
    vec2 dissolveCenterUV = uvDissolve.xy - vec2(0.5, 0.5);
    vec2 dissolvePolarUV = vec2(length(dissolveCenterUV) * 2.0, atan(dissolveCenterUV.y, dissolveCenterUV.x) / (2.0 * PI));
    uvDissolve.xy = dissolvePolarUV;
        #endif
    uvDissolve += fract(vec2(_DissolveTexUSpeed, _DissolveTexVSpeed) * _Time.y);
    vec4 var_DissolveTex = texture2D(_DissolveTex, uvDissolve.xy);
    float var_DissolveTexAlpha = var_DissolveTex.r;
    var_DissolveTexAlpha = clamp(pow(abs(var_DissolveTexAlpha), _DissolveTexPower) * _DissolveTexIntensity, 0.0, 1.0);
    vec3 DissolveColor = _DissolveColor.rgb * 2.0;
    float DissolveIntensity = _DissolveIntensity;
        //DissolveIntensity += 0.001; // Uncomment and adjust this value as needed
        #ifdef _DISSOLVETEXSOFT_ON
    float SoftDissolveBig = DissolveIntensity * (_DissolveSoft + 1.0);
    float SoftDissolveSmall = SoftDissolveBig - _DissolveSoft;
    float SoftDissolve = smoothstep(SoftDissolveSmall, SoftDissolveBig, var_DissolveTexAlpha);
    color = mix(DissolveColor, color, SoftDissolve);
    alpha *= SoftDissolve;
        #else
    float HardDissolve = DissolveIntensity * (_DissolveWide + 1.0);
    float HardDissolveSmall = step(HardDissolve, var_DissolveTexAlpha);
    float HardDissolveBig = step(HardDissolve - _DissolveWide, var_DissolveTexAlpha);
    color = mix(DissolveColor, color, HardDissolveSmall);
    alpha *= HardDissolveBig;
        #endif
    #endif

    color *= alpha;
    gl_FragColor = vec4(color, alpha);
}
